            ).join("\n\n");
            messages.push({
              role: "tool",
              content: `Action results:\n${toolResults}`,
              name: "file_operations"
            });

            // Auto-continue if there were reads and autopilot is on
            if (opts.autopilot) {
              console.log("ü§ñ Auto-continuing...\n");
              const followUp = await togetherChatCompletion({
                cwd,
