                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    email: document.getElementById('email').value,
                    password: document.getElementById('password').value,
                    subscriptionID: data.subscriptionID
                })
            }).then(response => {
                window.location.href = '/dashboard';
            });
        }
    }).render('#paypal-button-container');
};
document.body.appendChild(script);

// Add Apple Pay if available
if (window.ApplePaySession && ApplePaySession.canMakePayments()) {
    const applePayButton = document.createElement('button');
    applePayButton.className = 'apple-pay-button';
    applePayButton.innerHTML = 'Ô£ø Pay';
    applePayButton.addEventListener('click', handleApplePay);
    document.querySelector('.signup-form').insertBefore(applePayButton, document.querySelector('.terms'));
}

async function handleApplePay() {
    const session = new ApplePaySession(3, {
        countryCode: 'US',
        currencyCode: 'USD',
        merchantCapabilities: ['supports3DS'],
        supportedNetworks: ['visa', 'masterCard', 'amex', 'discover'],
        total: {
            label: 'Autopilot CLI Pro',
            amount: '15.00'
        }
    });

    session.onvalidatemerchant = async event => {
        const validationURL = event.validationURL;
        const response = await fetch('/api/apple-pay-validate', {
            method: 'POST',
            body: JSON.stringify({ validationURL })
        });
        const merchantSession = await response.json();
        session.completeMerchantValidation(merchantSession);
    };

    session.onpaymentauthorized = async event => {
        const payment = event.payment;
        const response = await fetch('/api/apple-pay-subscribe', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                email: document.getElementById('email').value,
                password: document.getElementById('password').value,
                payment: payment
            })
        });

        if (response.ok) {
            session.completePayment(ApplePaySession.STATUS_SUCCESS);
            window.location.href = '/dashboard';
        } else {
            session.completePayment(ApplePaySession.STATUS_FAILURE);
        }
    };

    session.begin();
}", "description": "Adding PayPal and Apple Pay options"}
