// src/api.ts
type Command = {
  id: string;
  text: string;
  status: 'pending' | 'running' | 'completed' | 'failed';
  output?: string;
};

type Plan = {
  id: string;
  commands: Command[];
  status: 'creating' | 'ready' | 'executing' | 'completed';
  questions: string[];
};

let currentPlan: Plan | null = null;

export async function createPlan(goal: string, executionMode: 'interactive' | 'continuous'): Promise<Plan> {
  // Simulate AI planning
  currentPlan = {
    id: Date.now().toString(),
    commands: [
      { id: '1', text: 'analyze project structure', status: 'pending' },
      { id: '2', text: 'identify dependencies', status: 'pending' },
      { id: '3', text: 'generate implementation plan', status: 'pending' },
    ],
    questions: executionMode === 'interactive' ? [
      'Should I use TypeScript or JavaScript for this implementation?',
      'Which testing framework would you prefer?'
    ] : [],
    status: executionMode === 'continuous' ? 'ready' : 'creating'
  };

  // Simulate planning delay
  await new Promise(resolve => setTimeout(resolve, 2000));

  if (executionMode === 'continuous') {
    currentPlan.status = 'ready';
    currentPlan.questions = []; // Continuous mode answers all questions automatically
  }

  return currentPlan;
}

export async function executePlan(planId: string): Promise<Plan> {
  if (!currentPlan || currentPlan.id !== planId) {
    throw new Error('Plan not found');
  }

  currentPlan.status = 'executing';

  // Execute each command
  for (const cmd of currentPlan.commands) {
    if (cmd.status === 'pending') {
      cmd.status = 'running';

      // Simulate command execution
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Randomly fail 10% of commands
      if (Math.random() < 0.1) {
        cmd.status = 'failed';
        cmd.output = 'Error: Command failed';
      } else {
        cmd.status = 'completed';
        cmd.output = 'Successfully executed';
      }
    }
  }

  currentPlan.status = 'completed';
  return currentPlan;
}

export async function answerQuestion(planId: string, questionId: string, answer: string): Promise<Plan> {
  if (!currentPlan || currentPlan.id !== planId) {
    throw new Error('Plan not found');
  }

  // Remove the answered question
  currentPlan.questions = currentPlan.questions.filter((_, idx) => `q${idx}` !== questionId);

  // If all questions answered, mark as ready
  if (currentPlan.questions.length === 0) {
    currentPlan.status = 'ready';
  }

  return currentPlan;
}

export async function getCurrentPlan(): Promise<Plan | null> {
  return currentPlan;
}
