```typescript
// server/services/AutopilotService.ts
import { AIPlanner } from './AIPlanner';
import { CodeExecutor } from './CodeExecutor';
import { Verifier } from './Verifier';
import { ErrorAnalyzer } from './ErrorAnalyzer';

type Session = {
  id: string;
  goal: string;
  state: ExecutionState;
  commands: Command[];
  currentPhase: string;
  error?: string;
  recoveryAttempts: number;
  verificationResults: VerificationResult[];
};

export class AutopilotService {
  private sessions: Record<string, Session> = {};
  private planner: AIPlanner;
  private executor: CodeExecutor;
  private verifier: Verifier;
  private errorAnalyzer: ErrorAnalyzer;

  constructor() {
    this.planner = new AIPlanner();
    this.executor = new CodeExecutor();
    this.verifier = new Verifier();
    this.errorAnalyzer = new ErrorAnalyzer();
  }

  async createSession(goal: string, mode: ExecutionMode): Promise<Session> {
    const sessionId = generateId();
    const session: Session = {
      id: sessionId,
      goal,
      state: 'planning',
      commands: [],
      currentPhase: 'Initializing',
      recoveryAttempts: 0,
      verificationResults: []
    };

    this.sessions[sessionId] = session;

    // Start planning in background
    this.planSession(sessionId);

    return session;
  }

  private async planSession(sessionId: string): Promise<void> {
    const session = this.sessions[sessionId];
    try {
      session.state = 'planning';
      session.currentPhase = 'Generating Plan';

      const plan = await this.planner.generatePlan(session.goal);
      session.commands = plan.commands.map(cmd => ({
        id: generateId(),
        command: cmd,
        status: 'pending',
        retries: 0
      }));

      session.state = 'executing';
      session.currentPhase = 'Ready for Execution';
    } catch (error) {
      session.state = 'error-recovery';
      session.error = `Planning failed: ${error.message}`;
      this.handleError(sessionId, error);
    }
  }

  async executeSession(sessionId: string): Promise<Session> {
    const session = this.sessions[sessionId];
    if (!session) throw new Error('Session not found');

    try {
      session.state = 'executing';

      // Execute all pending commands
      for (const cmd of session.commands.filter(c => c.status === 'pending' || c.status === 'failed')) {
        try {
          cmd.status = 'running';
          const output = await this.executor.execute(cmd.command);
          cmd.status = 'completed';
          cmd.output = output;
        } catch (error) {
          cmd.status = 'failed';
          cmd.output = error.message;

          if (cmd.retries < MAX_RETRIES) {
            cmd.retries++;
            return this.attemptRecovery(sessionId, cmd.id);
          } else {
            session.state = 'error-recovery';
            session.error = `Command failed after ${MAX_RETRIES} retries`;
            this.handleError(sessionId, error);
            return session;
          }
        }
      }

      // If all commands completed, move to verification
      if (session.commands.every(c => c.status === 'completed')) {
        session.state = 'verifying';
        session.currentPhase = 'Verifying Implementation';
        return this.verifySession(sessionId);
      }

      return session;
    } catch (error) {
      session.state = 'error-recovery';
      session.error = `Execution failed: ${error.message}`;
      this.handleError(sessionId, error);
      return session;
    }
  }

  private async verifySession(sessionId: string): Promise<Session> {
    const session = this.sessions[sessionId];
    try {
      const results = await this.verifier.verifyImplementation(session.goal);
      session.verificationResults = results;

      if (results.every(r => r.status === 'passed')) {
        session.state = 'completed';
        session.currentPhase = 'Implementation Complete';
      } else {
        session.state = 'error-recovery';
        session.error = 'Verification failed';
        this.handleError(sessionId, new Error('Verification failed'));
      }

      return session;
    } catch (error) {
      session.state = 'error-recovery';
      session.error = `Verification failed: ${error.message}`;
      this.handleError(sessionId, error);
      return session;
    }
  }

  async recoverCommand(sessionId: string, commandId: string): Promise<Session> {
    const session = this.sessions[sessionId];
    if (!session) throw new Error('Session not found');

    if (session.recoveryAttempts >= MAX_RECOVERY_ATTEMPTS) {
      session.state = 'error-recovery';
      session.error = 'Max recovery attempts reached';
      return session;
    }

    const cmd = session.commands.find(c => c.id === commandId);
    if (!cmd) throw new Error('Command not found');

    try {
      const recoveryPlan = await this.errorAnalyzer.analyzeAndRecover(
        cmd.command, 
        cmd.output || 'Unknown error'
      );

      // Insert recovery commands before the failed command
      const recoveryCommands = recoveryPlan.commands.map(c => ({
        id: generateId(),
        command: c,
        status: 'pending',
        retries: 0
      }));

      const cmdIndex = session.commands.indexOf(cmd);
      session.commands.splice(cmdIndex, 0, ...recoveryCommands);

      // Mark original command for retry
      cmd.status = 'pending';
      cmd.retries++;

      session.recoveryAttempts++;
      session.state = 'executing';
      session.currentPhase = 'Executing Recovery Plan';

      return session;
    } catch (error) {
      session.state = 'error-recovery';
      session.error = `Recovery failed: ${error.message}`;
      return session;
    }
  }

  private handleError(sessionId: string, error: Error): void {
    const session = this.sessions[sessionId];
    session.error = error.message;

    if (session.recoveryAttempts < MAX_RECOVERY_ATTEMPTS) {
      setTimeout(() => this.attemptAutoRecovery(sessionId), 2000);
    }
  }

  private async attemptAutoRecovery(sessionId: string): Promise<void> {
    const session = this.sessions[sessionId];
    session.recoveryAttempts++;

    try {
      const recoveryPlan = await this.errorAnalyzer.analyzeAndRecover(
        session.error || 'Unknown error'
      );

      // Add recovery commands to the beginning of the queue
      session.commands.unshift(...recoveryPlan.commands.map(c => ({
        id: generateId(),
        command: c,
        status: 'pending',
        retries: 0
      })));

      session.state = 'executing';
      session.currentPhase = 'Auto-Recovery in Progress';
      this.executeSession(sessionId);
    } catch (error) {
      session.error = `Auto-recovery failed: ${error.message}`;
    }
  }
}

function generateId(): string {
  return Math.random().toString(36).substring(2, 9);
}
