  async analyzeAndRecover(failedCommand: string, errorOutput: string): Promise<RecoveryPlan> {
    const systemPrompt = `You are an advanced error recovery system. Analyze the failed command and error output, then generate a recovery plan.

    Recovery Plan Requirements:
    1. Diagnose the root cause
    2. Create steps to fix the immediate issue
    3. Add preventative measures
    4. Include system state verification
    5. Plan for rollback if recovery fails

    Output format:
    {
      "diagnosis": "detailed explanation",
      "commands": ["step1", "step2"],
      "preventionStrategy": "how to prevent in future"
    }`;

    const response = await this.openai.chat.completions.create({
      model: 'gpt-4-1106-preview',
      messages: [
        { role: 'system', content: systemPrompt },
        { 
          role: 'user', 
          content: `Failed command: ${failedCommand}\nError output:\n${errorOutput}` 
        }
      ],
      temperature: 0.1,
      max_tokens: 2000,
      response_format: { type: 'json_object' }
    });

    const result = JSON.parse(response.choices[0].message.content!);
    return this.validateRecoveryPlan(result);
  }

  private validateRecoveryPlan(plan: any): RecoveryPlan {
    if (!plan.commands || !Array.isArray(plan.commands) || plan.commands.length === 0) {
      throw new Error('Recovery plan must contain executable commands');
    }

    if (!plan.diagnosis || typeof plan.diagnosis !== 'string') {
      throw new Error('Recovery plan must include a diagnosis');
    }

    return {
      commands: plan.commands,
      diagnosis: plan.diagnosis,
      preventionStrategy: plan.preventionStrategy || 'None provided'
    };
  }

  async getSystemState(): Promise<string> {
    const commands = [
      'node --version',
      'npm --version',
      'git status',
      'ls -la',
      'npm list'
    ];

    const results = await Promise.all(
      commands.map(cmd => this.executor.execute(cmd).catch(e => e.message))
    );

    return commands.map((cmd, i) => `$ ${cmd}\n${results[i]}`).join('\n\n');
  }
