// server/services/Verifier.ts
import OpenAI from 'openai';
import { CodeExecutor } from './CodeExecutor';

type VerificationResult = {
  test: string;
  status: 'passed' | 'failed';
  details: string;
};

export class Verifier {
  private openai: OpenAI;
  private executor: CodeExecutor;

  constructor() {
    this.openai = new OpenAI(process.env.OPENAI_API_KEY!);
    this.executor = new CodeExecutor();
  }

  async verifyImplementation(goal: string): Promise<VerificationResult[]> {
    const systemPrompt = `You are an advanced implementation verification system. Create a comprehensive set of tests to verify that the implementation meets all requirements.

    Verification Requirements:
    1. Functional correctness
    2. Performance characteristics
    3. Security compliance
    4. Error handling
    5. Edge case coverage

    For each test, provide:
    - Description of what's being tested
    - Command to execute the test
    - Expected output pattern`;

    const response = await this.openai.chat.completions.create({
      model: 'gpt-4-1106-preview',
      messages: [
        { role: 'system', content: systemPrompt },
        { role: 'user', content: `Implementation Goal: ${goal}` }
      ],
      temperature: 0.1,
      max_tokens: 3000,
      response_format: { type: 'json_object' }
    });

    const testPlan = JSON.parse(response.choices[0].message.content!);
    return this.executeTestPlan(testPlan);
  }

  private async executeTestPlan(testPlan: any): Promise<VerificationResult[]> {
    if (!testPlan.tests || !Array.isArray(testPlan.tests)) {
      throw new Error('Invalid test plan format');
    }

    const results: VerificationResult[] = [];

    for (const test of testPlan.tests) {
      try {
        const output = await this.executor.execute(test.command);
        const passed = this.checkTestOutput(output, test.expectedPattern);

        results.push({
          test: test.description,
          status: passed ? 'passed' : 'failed',
          details: passed ? 'Test passed successfully' : `Output did not match expected pattern. Actual output:\n${output}`
        });
      } catch (error) {
        results.push({
          test: test.description,
          status: 'failed',
          details: `Test execution failed: ${error.message}`
        });
      }
    }

    return results;
  }

  private checkTestOutput(output: string, expectedPattern: string): boolean {
    // Implement sophisticated output verification
    if (!expectedPattern) return true;

    try {
      const regex = new RegExp(expectedPattern, 's');
      return regex.test(output);
    } catch {
      return output.includes(expectedPattern);
    }
  }
}
