        filePath: `${component.name}.ts`,
        content: code,
        changeType: 'create',
        verificationStatus: 'unverified'
      });
    }

    return { changes: files, warnings: [] };
  }

  private async generateComponentCode(component: ComponentSpec): Promise<string> {
    const ast = this.astAnalyzer.createInitialAST(component);
    const optimizedAST = this.astAnalyzer.optimizeAST(ast);
    return this.astAnalyzer.generateCode(optimizedAST);
  }
}
