### 3. Intelligent Error Recovery System

```typescript
// src/core/error-recovery.ts
import { OpenAI } from 'openai';
import { CodeFixer } from './code-fixer';
import { TestAnalyzer } from './test-analyzer';

export class ErrorRecovery {
  private openai = new OpenAI(process.env.OPENAI_API_KEY!);
  private codeFixer = new CodeFixer();
  private testAnalyzer = new TestAnalyzer();

  async generateRecoveryPlan(task: AutonomousTask, error: Error): Promise<RecoveryPlan | null> {
    const context = {
      taskDescription: task.description,
      taskPhase: task.phase,
      codeChanges: task.codeChanges,
      error: error.message,
      previousErrors: task.errors
    };

    // First attempt: Fix based on error patterns
    const patternFix = this.codeFixer.attemptPatternFix(error, task.codeChanges);
    if (patternFix.success) {
      return {
        codeChanges: patternFix.changes,
        dependencies: [],
        recoveryStrategy: 'pattern-match'
      };
    }

    // Second attempt: Analyze test failures
    if (task.phase === 'testing') {
      const testFix = await this.testAnalyzer.analyzeAndFix(task.codeChanges);
      if (testFix.success) {
        return {
          codeChanges: testFix.changes,
          dependencies: testFix.dependencies,
          recoveryStrategy: 'test-analysis'
        };
      }
    }

    // Final attempt: Use AI to generate solution
    const response = await this.openai.chat.completions.create({
      model: 'gpt-4-turbo',
      messages: [
        {
          role: 'system',
          content: `Generate recovery plan for error: ${error.message}\n\nContext: ${JSON.stringify(context, null, 2)}`
        }
      ],
      temperature: 0.3,
      max_tokens: 4000
    });

    try {
      return JSON.parse(response.choices[0].message.content!) as RecoveryPlan;
    } catch {
      return null;
    }
  }
}
```

