### 5. React Component Integration

```tsx
// src/components/AutonomousCoder.tsx
import { useState, useEffect } from 'react';
import { AutonomousEngine } from '../core/autonomous-engine';
import { Terminal } from './Terminal';
import { TaskVisualizer } from './TaskVisualizer';

export function AutonomousCoder() {
  const [engine] = useState(() => new AutonomousEngine());
  const [currentTask, setCurrentTask] = useState<AutonomousTask | null>(null);
  const [executionLog, setExecutionLog] = useState<string[]>([]);
  const [ideaInput, setIdeaInput] = useState('');

  useEffect(() => {
    const interval = setInterval(() => {
      if (engine.currentTask) {
        setCurrentTask({...engine.currentTask});
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const submitIdea = async () => {
    if (!ideaInput.trim()) return;

    const task = await engine.submitIdea(ideaInput);
    setCurrentTask(task);
    setExecutionLog(prev => [...prev, `Started task: ${task.description}`]);
    setIdeaInput('');
  };

  return (
    <div className="autonomous-coder">
      <div className="control-panel">
        <h2>Autonomous Development System</h2>
        <div className="input-group">
          <input
            type="text"
            value={ideaInput}
            onChange={(e) => setIdeaInput(e.target.value)}
            placeholder="Describe what you want to build..."
            onKeyDown={(e) => e.key === 'Enter' && submitIdea()}
          />
          <button onClick={submitIdea}>Build It</button>
        </div>

        <div className="execution-settings">
          <label>
            <input type="checkbox" defaultChecked />
            Automatic Error Recovery
          </label>
          <label>
            <input type="checkbox" defaultChecked />
            Production Optimization
          </label>
        </div>
      </div>

      <div className="execution-view">
        <div className="task-visualization">
          {currentTask ? (
            <TaskVisualizer task={currentTask} />
          ) : (
            <div className="empty-state">
              <h3>No active task</h3>
              <p>Submit an idea to begin autonomous development</p>
            </div>
          )}
        </div>

        <div className="execution-terminal">
          <Terminal logs={executionLog} />
        </div>
      </div>
    </div>
  );
}
